import React, { useEffect, useState } from 'react';

function Products(props) {
    const [product,setProduct] = useState([])

    useEffect(() => {
        fetch('https://fakestoreapi.com/products').then(data => data.json()).then(result => setProduct(result))
        console.log("products details", product);
    }, [])
    return (
        <>
        <h1>this is product page</h1>
        {/* {JSON.stringify(product)} */}
        <div className='container-fluid'>
            <div className='row mt-5'>          
                
            {product.map((items, index) => (
                <div className='col-4 mb-5' key={index.id}>
                    <div className="card"  style={{ width: "18rem"}}>
                        <img src={items.image} className="card-img-top" alt="..." style={{ width: 100, }}/>
                        <div className="card-body">
                            <h5 className="card-title">{items.title}</h5>
                            <p className="card-text">{items.description}</p>
                            <a href="#" className="btn btn-primary">Add to Cart</a>
                        </div>
                    </div>
                </div>
            ))}
            </div>
        </div>
            
           
        </>
    );
}

export default Products;
